package com.example.gta5rplaws

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.widget.Toast
import androidx.appcompat.widget.SearchView
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import java.util.Locale

class ProcessLawActivity : AppCompatActivity() {

    private lateinit var recyclerViewProcess: RecyclerView
    private lateinit var searchViewProcess: SearchView
    private var mList = ArrayList<StatjaData>()
    private lateinit var adapter: StatjaAdapter

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_process_law)

            recyclerViewProcess = findViewById(R.id.recyclerViewProcess)
            searchViewProcess = findViewById(R.id.searchViewProcess)

            recyclerViewProcess.setHasFixedSize(true)
            recyclerViewProcess.layoutManager = LinearLayoutManager(this)

            addDataToList()

            adapter = StatjaAdapter(mList)
            recyclerViewProcess.adapter = adapter

            searchViewProcess.setOnQueryTextListener(object : SearchView.OnQueryTextListener {
                override fun onQueryTextSubmit(query: String?): Boolean {
                    return false
                }
                override fun onQueryTextChange(newText: String?): Boolean {
                    filterList(newText)
                    return true
                }
            })
        }
        private fun filterList(query: String?) {

            if (query != null) {
                val filteredList = ArrayList<StatjaData>()
                for (i in mList) {
                    if (i.statjaText.lowercase(Locale.ROOT).contains(query)){
                        filteredList.add(i)
                    }
                }
                if (filteredList.isEmpty()) {
                    Toast.makeText(this, "Не найдено!!!", Toast.LENGTH_LONG).show()
                } else {
                    adapter.setFilteredList(filteredList)
                }
            }

        }
